import React,{useReducer} from "react";

const initialState = {
    countOne : 0 ,
    countTwo : 0
}
const reducer = (state , action)=>{
    switch (action.type){
        case 'incrementOne': 
            return {countOne : state.countOne + action.value}
        case 'decrementOne' :
            return {countOne : state.countOne - action.value}
        case 'incrementTwo': 
            return {countTwo : state.countOne + action.value}
        case 'decrementTwo' :
            return {countOne : state.countOne - action.value}
    }
}


const Count = ()=>{
    const [state , dispatch]= useReducer(reducer , initialState)
    return(
        <div className='row'>
            <div className="col-6">
                <h1>hh</h1>  
                <button className="btn btn-success m-3" onClick={()=> dispatch({type : 'incrementOne' , value: 1})}>+</button>   
                <button className="btn btn-danger m-3" onClick={()=> dispatch({type : 'decrementOne' , value: 1})}>-</button>   
            </div>
            <div className="col-6">
                <h1>hh</h1>  
                <button className="btn btn-success m-3" onClick={()=> dispatch({type : 'incrementTwo' , value: 10})}>+</button>   
                <button className="btn btn-danger m-3" onClick={()=> dispatch({type : 'decrementTwo' , value: 10})}>-</button>   
            </div>
        </div>
    )
}
export default Count
