import React , {useState}from "react";
import ListTask from "./ListTask";
import axios from 'axios'
import { URI } from "../utils/API";
const Form = () => {

    const [taskInput , setTaskInput] = useState("")
    


    const addTask = ()=>{
        axios.post(`${URI}/save` ,{task :taskInput} )
            .then(response=>{
                console.log('task added')
            }).catch(err=>{
                console.log(err)
            })


    }

   

    return (
        <>
            <div className=" d-flex justify-content-center">
                <div>
                    <input  value={taskInput} type="text" className="form-control" onChange={(e)=>setTaskInput(e.target.value)} />
                </div>
                <div>
                    <button className="btn btn-primary" onClick={addTask} style={{ "marginLeft": "5px" }}>Add new task</button>
                </div>
            </div>
            <ListTask/>
        </>
    );
};

export default Form;
