import React , {useState} from "react";
import FunctionUseEffect from "./FunctionUseEffect";
const Container =() =>{

    const [show , setShow] =  useState(true);
    const displayTitle = show ? 'Cacher':'Afficher'
    return (
        <div className='App'>
            <button className="btn btn-primary" onClick={()=> setShow(!show)}>{displayTitle}</button>
            <FunctionUseEffect/>
        </div>
    )

    
}
export default Container
