import React,{useReducer} from "react";

const initialState  = {
    count : 0
}; 
const reducer = (state , action )=>{
    switch (action.type) {
        case 'incrementation':
            return {count : state.count + action.value};
        case 'decrementation':
            return {count : state.count - action.value}
    }

}
const Count = ()=>{
    const[state , dispatch] = useReducer(reducer , initialState)
    return(
        <div className='App'>
            <h1>{state.count}</h1>  
            <button className="btn btn-success m-3" onClick={()=> dispatch({type : 'incrementation',value : 1})} >+</button>   
            <button className="btn btn-danger m-3" onClick={()=> dispatch({type : 'decrementation',value : 1})}>-</button>   
        </div>
    )
}
export default Count
