import React , {Component, component} from "react";
import countHits from "./CountHits";
import vegeta from './../images/vegeta.png'

class Vegeta extends Component{
    
    render(){
        const {name , addOneHit, hocState , life} = this.props
        const lifeValue = life > 0 ? (<td>{life} %</td>):(<td><span className="text-danger" style={{fontWeight :'bold'}}>Mort</span></td>)
        const button = life > 0 ? (<button className="btn btn-success m-3" onClick={addOneHit}>{name} frappe</button>) : (<button className="btn btn-danger m-3 disabled" >Mort</button>)
        return(
            <div className="col">
                <img src={vegeta} alt="vegeta"/><br/>
                {button}
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Count:</th>
                            <th>Vie:</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{hocState.hits}</td>
                            {lifeValue}
                        </tr>
                    </tbody>

                </table>
            </div>
        )
    }
}
export default countHits(Vegeta ,)
