import React from 'react'
import axios from 'axios'
import { URI } from '../utils/API'

const deleteTask = (id)=>{
    axios.delete(`${URI}/delete/${id}`)
        .then(Response=>{
            console.log('task delete successfully')
            setUpdateUI((prevState))
        }).catch(err=>{
            console.log(err)
        })
}


const ListTask = ({ tasks, setUpdateUI }) => {
    return (
        <div className='col-md-6 col-lg-6 col-sm-8  mt-2 card ' style={{ "margin": "auto" }}>
            <ul className="list-group">
                {
                    tasks.map((task) => {
                        return (
                            <li key={task._id} className="list-group-item d-flex justify-content-between align-items-center">
                                {task.task}
                                <span><button className='btn btn-default'>Edit</button> <button className='btn btn-danger' onClick={deleteTask}>Delete</button></span>
                            </li>
                        )

                    })
                }

            </ul>
        </div>
    )
}

export default ListTask
