import React  from "react";
import ProfileInfo from './ProfileInfo'
class ProfileContent extends React.Component{
    state= {
        display : false
    }
    handleDisp = ()=>{
        if(this.state.display){
            this.setState({
                display : false
            })
        }else{
            this.setState({
                display : true
            })
        }
    }
    render(){
      profile = this.state.display ? (<ProfileInfo />): null
        return (
            <div>
                <p>A propos de xxxx</p>
                <button  onClick={this.handleDisp} className="btn btn-primary">Afficher l'information...</button>
                {profile}
            </div>
        )
    }
}
export default ProfileContent
