import React , {useState , useEffect , useContext} from "react";

const FunctionUseEffect =() =>{
    const [keyCode , setKeyCode] = useState('')

    
    useEffect(()=>{
        document.addEventListener ("keyup", (e)=>{
            setKeyCode(e.code)
        })

        return ()=>{
            document.removeEventListener ("keyup", (e)=>{
                setKeyCode('e.code')
            })
        }
    } , [])
    
    return (
        <div className='App'>
            <div className='card'>
                <div className='card-body'>
                    {keyCode}
                </div>
            </div>
            <div className="card">
            <div className='card-body'>
                    
                </div>
            </div>
        </div>
        
    )

}
export default FunctionUseEffect
