import React, {Component} from "react";
import { isDOMComponent } from "react-dom/test-utils";
import goku from './../images/Goku.png'
isDOMComponent
class Goku extends Component{
    state= {
        bg:''
    }
    handleclick = ()=>{
        this.setState({
            bg : 'bg-success'
        })
    }
    render(){
        return (
            <div className={`col ${this.state.bg}`}>
                <img  onClick={this.handleclick} src={goku} alt="Goku"/>
            </div>
        )
    }
}
export default Goku
