import React, { useEffect, useState } from "react";
import ListTask from "./ListTask";
import axios from 'axios'
import { URI } from "../utils/API";
const Form = () => {

    const [taskInput, setTaskInput] = useState("")
    const [updateUI, setUpdateUI] = useState(false)
    const [tasks, setTasks] = useState([])

    useEffect(() => {
        axios.get(`${URI}/get`)
            .then(response => {
                setTasks(response.data)
            })

    }, [updateUI])

    const addTask = () => {
        axios.post(`${URI}/save`, { "task": taskInput })
            .then(response => {
                console.log(response.data)
                setUpdateUI(!updateUI)
                setTaskInput("")
            }).catch(err => {
                console.log(err)
            })
    }



    return (
        <>
            <div className=" d-flex justify-content-center">
                <div>
                    <input value={taskInput} type="text" className="form-control" onChange={(e) => setTaskInput(e.target.value)} />
                </div>
                <div>
                    <button type='submit' className="btn btn-primary" onClick={addTask} style={{ "marginLeft": "5px" }}>Add new task</button>
                </div>
            </div>
            <ListTask  setTaskInput={setTaskInput} setUpdateUI={setUpdateUI} tasks={tasks} />
        </>
    );
};

export default Form;
