import React , {useState , useEffect} from "react";

const FunctionUseEffect =() =>{
    const [keyCode , setKeyCode] = useState('')

    const handleKeyCode  = (e)=>{
        setKeyCode(e.code)
    }
    useEffect(()=>{
        document.addEventListener ("keyup", handleKeyCode)
        return ()=>{
            document.dEventListener ("keyup", handleKeyCode)

        }
    } , [])
    return (
        <div className='App'>
            <div className='card'>
                <div className='card-body'>
                    {keyCode}
                </div>
            </div>
        </div>
    )

}
export default FunctionUseEffect
