import React,{useReducer} from "react";

const initialState = 0;

const reducer = (state , action)=>{
    switch (action){
        case('increment') : 
            return state +1
        case('decrement'): 
            return state -1
        default :
            return state
    }
    
}

const Count = ()=>{
    const [count , dispach] = useReducer(reducer , initialState)
    return(
        <div className='App'>
            <h1>{count}</h1>  
            <button className="btn btn-success" onClick={()=>dispach('')}>+</button>   
            <button className="btn btn-danger">-</button>   

        </div>
    )
}
export default Count
