import React , {Component} from 'react';
import './App.css';
import Profile  from './Components/Profile';
import {userContext} from './Components/MyContexts'
import {colorContext} from './Components/MyContexts'
import {professeurContext} from './Components/MyContexts'
class App extends Component {

  state ={
    user : {
      name : "Jean", 
      age : 12 
    },
    color : "red", 
    professeur : {
      name: "Solo",
      role: "responsable"
    }
  }
  render(){
    return (
      <professeurContext.Provider  value={this.state.professeur}>
        <userContext.Provider value = {this.state.user}>
          <colorContext.Provider value={this.state.color}>

            <div className='App'>
              <Profile/>
            </div>
          </colorContext.Provider>
        </userContext.Provider>
       </professeurContext.Provider>
    );
  }
}
export default App;
