import React , {Component} from "react";
import countHits from "./CountHits";
import goku from './../images/Goku.png'

class Goku extends Component{
    state= {
        hits: 0
    }
    addOne = ()=>{
        this.setState(prevStat =>{
            return {hits: prevStat.hits + 1}
        })
    }
    render(){
        return(
            <div className="col">
                <img src={goku} alt="Goku"/><br/>
                <button className="btn btn-success m-3" onClick={this.addOne}> {this.props.name}  frappe</button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Count:</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>Coup :{this.state.hits}</td>
                        </tr>
                    </tbody>
                </table>
            </div>
        )
    }
}
export default countHits(Goku)
