import React,{useReducer} from "react";

const initialCounter = 0 ;

const reducer = (count , action)=>{
    switch (action){
        case 'increment':
            return count +1
        case 'decrement':
            return count -1
        default :
            return count
    }
}
const Count = ()=>{
    const [count , dispach] = useReducer(reducer , initialCounter)
    return(
        <div className='App'>
            <h1>{count}</h1>  
            <button className="btn btn-success m-3" onClick={()=> dispach('increment')} >+</button>   
            <button className="btn btn-danger m-3" onClick={()=> dispach('decrement')}>-</button>   
        </div>
    )
}
export default Count
